const invertedIndex = JSON.parse(jsonEncodedIndex);

function buildKeyTree(invertedIndex) {
	const keyTree = {};

  Object.keys(invertedIndex).forEach(el => {
    const firstLetter = el[0];
    let data = keyTree[firstLetter];
    if (!data) {
      data = [];
    }
    keyTree[firstLetter] = data.concat(el);
  });

  return keyTree;
}

const keyTree = buildKeyTree(invertedIndex);

// /////////////////////////////////////////////////////////
// Search logic
// /////////////////////////////////////////////////////////

/**
 * Translate a query like 'face with' into a an array of words
 * that we can match against an invertedIndex:
 *
 * face with => [["face"], ["with", "without"]]
 *
 * The elements of the array should be combined into an AND
 * search: invertedIndex[face] && (invertedIndex[with] && invertedIndex[without]).
 */
findWordsForSearch = searchQuery => {
  return searchQuery
    .split(' ')
    .filter(el => el.length > 0) // Remove empty elemens, they will break us
    .map(search => {
        const firstLetter = search[0];
        let possibleMatches = keyTree[firstLetter];

        return possibleMatches.filter(match => match.startsWith(search));
    });

}

const translateToEmoji = word => invertedIndex[word];

// Translates a search word from the index to a list of matching emojis
function lookupSearch(search) {
  const searchWords = findWordsForSearch(search);
  // Translate all search words into an array of the respective emojis
  // [face], [with, without] => [all emojis with face], [all emojis with+without]
  const possibleMatches = searchWords.map(word => word.map(translateToEmoji).flat());

  // No intersect the arrays, what remains are emojis that match both words
  return possibleMatches.reduce((result, array) => {
    return result.filter(el => array.includes(el));
  }, possibleMatches[0]);
}

function searchResultToSuggestEntry(emoji) {
  return {emoji, label: emojis[emoji] };
}


function searchSuggest(search) {
  const searchResults = lookupSearch(search);
  return searchResults.map(searchResultToSuggestEntry);
}

