const state = {
  text: '',
  suggest: false
};

// /////////////////////////////////////////////////////////
// Suggest Box stuff
// /////////////////////////////////////////////////////////
function hideSuggestBox() {
  state.suggest = false;
  suggestBox.style.display = 'none';
}

function positionSuggestBox(target) {
  const rect = target.getBoundingClientRect();
  suggestBox.style.bottom = (rect.height + 20) + 'px';
	suggestBox.style.left = rect.left + 'px';
}

