// /////////////////////////////////////////////////////////

function insertEmoji(text, emoji) {
  const lastIndexOf = text.lastIndexOf(':');
  return text.substr(0, lastIndexOf) + emoji;
}

function renderEmojiTrigger(target, data) {
  const a = document.createElement('a');
  a.style.padding = '2px';
  a.textContent = data.emoji + ' ' + data.label;
  a.addEventListener('click', () => {
    target.value = insertEmoji(target.value, data.emoji)
    target.focus();
    hideSuggestBox();
  });
  suggestBox.appendChild(a);
}

function getSearchWord(text) {
  const lastIndex = text.lastIndexOf(':');
  return text.substr(lastIndex + 1);
}

// This doesn't care about unicode right now
function sortByLabelLength(firstEl, secondEl) {
  // I can't remember why, but this also bad.
  return firstEl.label.length - secondEl.label.length;
}

function updateSuggest(target) {
  positionSuggestBox(target);
  suggestBox.textContent = '';
  if (state.suggest) {
    suggestBox.style.display = "block";
  } else {
    hideSuggestBox();
  }

  const searchWord = getSearchWord(state.text);
  const suggestions= searchSuggest(searchWord);
  if (suggestions.length === 0) {
    suggestBox.textContent = "No results."
  } else {
    suggestions
      .sort(sortByLabelLength)
      .forEach(data => renderEmojiTrigger(target, data));
    suggestBox.firstChild.style.background = "orange";
  }
}


function inSuggestMode(currentKey, currentValue) {
    const lastIndexOf = currentValue.lastIndexOf(':');

    // Colon is not found
    if (lastIndexOf === -1) {
        return false;
    }

    const charactersAfterColon = currentValue.substr(lastIndexOf + 1).length;

    // Colon is first and only character
    if (lastIndexOf === 0 && charactersAfterColon >= 1) {
        return true;
    }

    // Check if we have a "text :search" or "text: text" situation.
    const spaceBeforeColon = currentValue[lastIndexOf - 1] === ' ';
    const spaceAfterColon = currentValue[lastIndexOf + 1] === ' ';
    const spaceAroundColon = spaceAfterColon && spaceBeforeColon;

  	const colonPositionIsOk = !spaceAroundColon && (spaceBeforeColon || (!spaceBeforeColon && !spaceAfterColon));

    return charactersAfterColon >= 1 && colonPositionIsOk;
}

