// Hooks to trigger on interaction
// /////////////////////////////////////////////////////////

function composerChangeHandler(ev) {
    const currentKey = ev.key;

    // Ignore shift, ctrl etc.
    if (currentKey !== "Backspace" && currentKey.length > 2) {
        return;
    }

    state.text = ev.target.value;
    state.suggest = inSuggestMode(currentKey, ev.target.value);

    updateSuggest(ev.target);
    console.log(state);
}

function insertEmojiHandler(ev) {
    const currentKey = ev.key;
    const value = ev.target.value.trim();

    // Make sure not to trigger on other behaviour
    if (value === '!jitsi') {
        return;
    }

    if (currentKey === "Tab") {
        suggestBox.firstChild.click();
        ev.preventDefault();
    }
}

const COMPOSER_CLASS = 'jc-ng-chat-message-composer';

// Use this class name as trigger to check for a composer to register onChange
const COMPOSER_TRIGGER_CLASS = 'jc-ng-chat-message-composer-container';

/*
 * Look for potential composers to watch.
 */
function _findComposers(ev) {
    const el = ev.target;
	  const composers = el.getElementsByClassName(COMPOSER_CLASS);
    if (composers.length === 0) {
        console.info("No composers found in composer container.");
        return [];
    } else {
        return Array.from(composers);
    }
}

/**
 * Check if the element is a composer and if so, register the required handlers.
 */
function composerWatcher(el) {
    const className = el.target.className;
    if (className !== COMPOSER_TRIGGER_CLASS) {
        return;
    }

    const composers = _findComposers(el);
    composers.forEach(e => {
        e.addEventListener('keyup', composerChangeHandler);
        // With tab, there is no keyup
        e.addEventListener('keydown', insertEmojiHandler);
    });
}

document.addEventListener("DOMNodeInserted", composerWatcher, false);
